    public String remplazoO(String palabra) {
        String remplazo = palabra.replace("o", "$").replace("O", "$");
        System.out.println("Palabra modificada: " + remplazo);
        return remplazo;
    }

    public String remplazoA(String palabra) {
        String remplazo = palabra.replace("A", "@").replace("a", "@");
        System.out.println("Palabra modificada: " + remplazo);
        return remplazo;
    }

    public String Alrevez() {

        return null;

    }

    public int contarPalabras(String palabra) {
        int p = 0;
        for (int i = 0; i < palabra.length(); i++) {
            char c = palabra.charAt(i);
            if (c == ' ') {
                p++;
            }
        }
        int palabras = p + 1;
        System.out.println("El número de espacios es: " + p);
        System.out.println("El numero de palabras es: " + palabras);
        return palabras;
    }

    public int contarEspacio(String palabra) {
        int p = 0;
        for (int i = 0; i < palabra.length(); i++) {
            char c = palabra.charAt(i);
            if (c == ' ') {
                p++;
            }
        }

        System.out.println("El número de espacios es: " + p);
        return p;
    }

    public String oracionAlrevez(String palabra) {
        String invertida = "";
        for (int i = palabra.length()-1 ; i >= 0; i--) {
            invertida += palabra.charAt(i);

        }
        System.out.println(invertida);
        return invertida;

    }
