    private void txtTextoKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtTextoKeyPressed
        String Palabra = txtTexto.getText();
        txtA.setEditable(false);
        txtAlrevez.setEditable(false);
        txtEspacio.setEditable(false);
        txtNumero.setEditable(false);
        txtO.setEditable(false);
        String RemplazoO = remplazoO(Palabra);
        String RemplazoA = remplazoA(Palabra);
        int numero = contarPalabras(Palabra);
        int Espacio = contarEspacio(Palabra);
        String numero2 = Integer.toString(numero);
        String Espacio1 = Integer.toString(Espacio);
        String oracionAlrevez = oracionAlrevez(Palabra);
        txtO.setText(RemplazoO);
        txtA.setText(RemplazoA);
        txtNumero.setText(numero2);
        txtEspacio.setText(Espacio1);
        txtAlrevez.setText(oracionAlrevez);
    }//GEN-LAST:event_txtTextoKeyPressed

